        addMeal(Meal(value:["ID":251,"Catagory":"", "CookTime":"", "Ingredients":"about 3 medium heirloom tomatoes\nolive oil\nsherry vinegar\n½ cup uncooked whole wheat orzo\n1 clove of garlic\na bit handful of arugula\n½ cup chickpeas, cooked & drained\n½ large avocado, diced\njuice of ½ a small lemon\nhandful of fresh basil\npine nuts\nsalt & pepper", "Name":"heirloom tomato, & avocado salad", "Notes":"gluten free: sub out orzo for quinoa (adjust cooking method accordingly).", "Others":"", "OthersName":"", "PrepTime":"", "Serve":"serves 2 as a main, 3-4 as a side", "TotalTime":"", "img":"http://d1wcgy4dy6voh7.cloudfront.net/wp-content/uploads/2013/08/IMG_0061.jpg", "url":"http://www.loveandlemons.com/2013/08/15/heirloom-tomato-avocado-chickpea-salad/"]))
